SELECT strftime('%Y-%m-%d', date_time) as date , profile_sites.abbreviation, profile_sites.id, AVG(temp) as temp, AVG(ph) as ph, AVG(sp_cond) as sp_cond, AVG(salinity) as salinity, AVG(dissolved_oxygen) as dissolved_oxygen, AVG(dissolved_oxygen_percent) as dissolved_oxygen_percent, AVG(dep_25) as dep_25, AVG(par) as par, AVG(rpar) as rpar, AVG(turbidity_sc) as turbidity_sc, avg(chl) as chl, avg(chl_volts) as chl_volts FROM vertical_profiles, profile_sites WHERE date_time > '2015-01-01 00:00:00.000000' and date_time < '2015-06-01 00:00:00.000000' and dep_25 < 1 AND profile_sites.id = vertical_profiles.profile_site_id GROUP BY date, profile_site_id 